import { define } from '@coffeekraken/s-filtrable-input-component';
define({
    items: async () => {
        // you can get your items however you want
        // const request = await fetch('...');
        // const items = await request.json();
        return [{title: 'Hello', value: 'World'},{title: 'Plop', value:'Yop}];
    },
    templates: ({ type, html }) => {
        switch (type) {
            case 'item':
                return html`
                    <li class="__item">
                        My title: ${item.title}
                    </li>
                `;
                break;
            case 'loading':
                return html`
                    <li class="__loading">
                        Loading, please wait...
                    </li>
                `;
                break;
            case 'empty':
                return html`
                    <li class="__empty">
                        No items found...
                    </li>
                `;
                break;
        }
    },
}, 'my-cool-filtrable-input');

