@use '@coffeekraken/s-filtrable-input/index' as SFiltrableInput;
@include SFiltrableInput.style();

$primaryColor: #15191b;
$secondaryColor: #2b3438;


































.s-sugar-ui {
    position: fixed;
    top: 0; left: 0;
    width: 100vw;
    height: 100vh;
    z-index: 1000000;
    display: block;
    pointer-events: none;

    &:before {
        content: '';
        position: absolute;
        top: 0; left: 0;
        width: 100vw;
        height: 100vh;
        transition: all ease-in-out .2s;
        opacity: 0;
        @include Sugar.background-grid-1x1(
            $color: Sugar.color($primaryColor, -opacity 10%)
        );
    }
    &:focus-within:before {
        opacity: 1;
    }
}

    .s-sugar-ui__coffeekraken-logo {
        @include Sugar.coffeekraken-logo(
            $color: white
        );
        width: 100px;
        margin-right: Sugar.lnf(padding-horizontal);
    }

    .s-sugar-ui__main {
        position: absolute;
        top: 0; left: 0;
        width: 100vw;
        height: 70px;
        background: $primaryColor;
        padding: 0 Sugar.lnf(padding-horizontal);
        display: flex;
        flex-wrap: nowrap;
        align-items: center;
        pointer-events: all;

        transition: all ease-in-out .2s;
        transform: translateY(-100%);
        &:focus-within {
            transform: translateY(0);
        }

        .s-filtrable-input {
            height: 100%;
            width: 100%;
        }
    }

    .s-sugar-ui__nav {
        height: 100%;
        width: 100%;
    }
        .s-sugar-ui__nav-input {
            padding: 0 Sugar.lnf(padding-horizontal);
            color: white;
            font-size: 20px;
            background: $secondaryColor;
            border:none;
            height: 100%;
            width: 100%;
        }

    .s-sugar-ui-input-navigation__list {
        background: white;
        display: block !important;
        opacity: 1 !important;
        pointer-events: all !important;
    }
        .s-sugar-ui-input-navigation__list-loading {
            padding: Sugar.lnf(padding);
            min-width: 100%;
          }
            .s-sugar-ui-input-navigation__list-loading-icon {
              @include Sugar.loader-spinner(
                $color: Sugar.color(primary),
                $size: 1em
              );
              display: inline-block;
            }
            .s-sugar-ui-input-navigation__list-loading-text {
              white-space: nowrap;
            }

          .s-sugar-ui-input-navigation__list-no-item {
            padding: Sugar.lnf(padding);
            min-width: 100%;
          }
            .s-sugar-ui-input-navigation__list-no-item-text {
              white-space: nowrap;
            }

            .s-sugar-ui-input-navigation__list-item {
                padding: Sugar.lnf(padding);

                &:hover {
                    background: Sugar.color($secondaryColor, -opacity 3%);
                }
                &.s-sugar-ui-input-navigation__list-item--preselected {
                    background: Sugar.color($secondaryColor, -opacity 7%);
                }

            }
          .s-sugar-ui-input-navigation__list-item--selected {
            background-color: Sugar.color($secondaryColor) !important;
            color: white;
          }
          .s-sugar-ui-input-navigation__list-item-title {
            font-weight: bold;
            @include Sugar.text-ellipsis();
            margin-bottom: 5px;
          }
          .s-sugar-ui-input-navigation__list-item-description {
              font-size: 12px;
            @include Sugar.text-ellipsis();
          }
          .s-sugar-ui-input-navigation__list-item-extension {
              display: inline-block;
              height: 20px;
              line-height: 20px;
              font-size: 12px;
              font-weight: bold;
              padding: 0px 4px 4px 4px;
              position: relative;
              top: -2px;
              background-color: Sugar.color(extension);
              color: white;

              $extensionModifiers: Sugar.setting('colors.extension.modifiers');
              @each $ext, $color in $extensionModifiers { 
                &[extension="#{$ext}"] {
                    background-color: $color;
                }
              }
          }
          .s-sugar-ui-input-navigation__list-item-highlight {
            background-color: Sugar.color(primary);
            color: white;
            position: relative;
            display: inline-block;
            padding: 0 4px;






















