.s-sugar-ui__coffeekraken-logo:after {
  content: "";
  display: block;
  width: 100%;
  height: 0;
  padding-bottom: 30%;
}
.s-sugar-ui__main {
  position: absolute;
  top: 0;
  left: 0;
  width: 100vw;
  height: 70px;
  background: #15191b;
  padding: 0 1.4em;
  display: flex;
  flex-wrap: nowrap;
  align-items: center;
  pointer-events: all;
  transition: all ease-in-out 0.2s;
  transform: translateY(-100%);
}
.s-sugar-ui__main:focus-within {
  transform: translateY(0);
}
.s-sugar-ui__main .s-filtrable-input {
  height: 100%;
  width: 100%;
}
.s-sugar-ui__nav {
  height: 100%;
  width: 100%;
}
.s-sugar-ui__nav-input {
  padding: 0 1.4em;
  color: white;
  font-size: 20px;
  background: #2b3438;
  border: none;
  height: 100%;
  width: 100%;
}
.s-sugar-ui-input-navigation__list {
  background: white;
  display: block !important;
  opacity: 1 !important;
  pointer-events: all !important;
}
.s-sugar-ui-input-navigation__list-loading {
  padding: 0.8em 1.4em;
  min-width: 100%;
}
.s-sugar-ui-input-navigation__list-loading-icon {
  display: inline-block;
}
.s-sugar-ui-input-navigation__list-loading-icon {
  display: inline-block;
  font-size: 1em;
  position: relative;
  top: -0.5em;
  left: -0.5em;
  text-indent: -9999em;
  border-top: 0.3em solid rgba(242, 188, 43, 0.2);
  border-right: 0.3em solid rgba(242, 188, 43, 0.2);
  border-bottom: 0.3em solid rgba(242, 188, 43, 0.2);
  border-left: 0.3em solid #f2bc2b;
  animation: spinner-uta9zld 1s linear 0s infinite;
}
.s-sugar-ui-input-navigation__list-loading-icon, .s-sugar-ui-input-navigation__list-loading-icon:after {
  border-radius: 50%;
  width: 1em;
  height: 1em;
}
@keyframes spinner-uta9zld {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}
.s-sugar-ui-input-navigation__list-loading-text {
  white-space: nowrap;
}
.s-sugar-ui-input-navigation__list-no-item {
  padding: 0.8em 1.4em;
  min-width: 100%;
}
.s-sugar-ui-input-navigation__list-no-item-text {
  white-space: nowrap;
}
.s-sugar-ui-input-navigation__list-item {
  padding: 0.8em 1.4em;
}
.s-sugar-ui-input-navigation__list-item:hover {
  background: rgba(43, 52, 56, 0.03);
}
.s-sugar-ui-input-navigation__list-item.s-sugar-ui-input-navigation__list-item--preselected {
  background: rgba(43, 52, 56, 0.07);
}
.s-sugar-ui-input-navigation__list-item--selected {
  background-color: #2b3438 !important;
  color: white;
}
.s-sugar-ui-input-navigation__list-item-title {
  font-weight: bold;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
  margin-bottom: 5px;
}
.s-sugar-ui-input-navigation__list-item-description {
  font-size: 12px;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
}
.s-sugar-ui-input-navigation__list-item-extension {
  display: inline-block;
  height: 20px;
  line-height: 20px;
  font-size: 12px;
  font-weight: bold;
  padding: 0px 4px 4px 4px;
  position: relative;
  top: -2px;
  background-color: #f2bc2b;
  color: white;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=blade] {
  background-color: #ff2d20;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=php] {
  background-color: #8892BF;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=js] {
  background-color: #f7df1e;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=node] {
  background-color: #68A063;
}
