  background-color: #CF649A;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=sass] {
  background-color: #CF649A;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=json] {
  background-color: #000000;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=jpg] {
  background-color: #B2C0E1;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=jpeg] {
  background-color: #B2C0E1;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=pdf] {
  background-color: #E7786E;
}
