// 	coco : universe
// )

Sugar.map-filter-by-namespace((
 'hello.world': 'Hello',
 'coco.world': 'Coco',
 'hello.that': 'That is cool'
), 'hello');
// return that map:
(
  'hello.world': 'Hello',
  'hello.that': 'That is cool'
)

$map: (
   something: (
      cool: 'Hello world'
   )
)
map-flatten($map);
(
