body {
  animation-duration: 0.001s;
}

@media (min-width: 0px) {
  body {
    animation-name: mq-mobile;
  }
}
@keyframes mq-mobile {
  from {
    clip: rect(1px, auto, auto, auto);
  }
  to {
    clip: rect(0px, auto, auto, auto);
  }
}
@media (min-width: 640px) {
  body {
    animation-name: mq-tablet;
  }
}
@keyframes mq-tablet {
  from {
    clip: rect(1px, auto, auto, auto);
  }
  to {
    clip: rect(0px, auto, auto, auto);
  }
}
@media (min-width: 1280px) {
  body {
    animation-name: mq-desktop;
  }
}
@keyframes mq-desktop {
  from {
    clip: rect(1px, auto, auto, auto);
  }
  to {
    clip: rect(0px, auto, auto, auto);
  }
}
