}
.s-sugar-ui__coffeekraken-logo:after {
  content: "";
  display: block;
  width: 100%;
  height: 0;
  padding-bottom: 30%;
}
.s-sugar-ui__main {
  position: absolute;
  top: 0;
  left: 0;
  width: 100vw;
  height: 70px;
  background: #15191b;
  padding: 0 1.4em;
  display: flex;
  flex-wrap: nowrap;
  align-items: center;
  pointer-events: all;
  transition: all ease-in-out 0.2s;
  transform: translateY(-100%);
}
.s-sugar-ui__main:focus-within {
  transform: translateY(0);
}
.s-sugar-ui__main .s-filtrable-input {
  height: 100%;
  width: 100%;
}
.s-sugar-ui__nav {
  height: 100%;
  width: 100%;
}
.s-sugar-ui__nav-input {
  padding: 0 1.4em;
  color: white;
  font-size: 20px;
  background: #2b3438;
  border: none;
  height: 100%;
  width: 100%;
}
.s-sugar-ui-input-navigation__list {
  background: white;
  display: block !important;
  opacity: 1 !important;
  pointer-events: all !important;
}
.s-sugar-ui-input-navigation__list-loading {
  padding: 0.8em 1.4em;
  min-width: 100%;
}
.s-sugar-ui-input-navigation__list-loading-icon {
  display: inline-block;
}
.s-sugar-ui-input-navigation__list-loading-icon {
  display: inline-block;
  font-size: 1em;
  position: relative;
  top: -0.5em;
  left: -0.5em;
  text-indent: -9999em;
  border-top: 0.3em solid rgba(242, 188, 43, 0.2);
  border-right: 0.3em solid rgba(242, 188, 43, 0.2);
  border-bottom: 0.3em solid rgba(242, 188, 43, 0.2);
  border-left: 0.3em solid #f2bc2b;
  animation: spinner-u3jzf6y 1s linear 0s infinite;
}
.s-sugar-ui-input-navigation__list-loading-icon, .s-sugar-ui-input-navigation__list-loading-icon:after {
  border-radius: 50%;
  width: 1em;
  height: 1em;
}
@keyframes spinner-u3jzf6y {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}
.s-sugar-ui-input-navigation__list-loading-text {
  white-space: nowrap;
}
.s-sugar-ui-input-navigation__list-no-item {
  padding: 0.8em 1.4em;
  min-width: 100%;
}
.s-sugar-ui-input-navigation__list-no-item-text {
  white-space: nowrap;
}
.s-sugar-ui-input-navigation__list-item {
  padding: 0.8em 1.4em;
}
.s-sugar-ui-input-navigation__list-item:hover {
  background: rgba(43, 52, 56, 0.03);
}
.s-sugar-ui-input-navigation__list-item.s-sugar-ui-input-navigation__list-item--preselected {
  background: rgba(43, 52, 56, 0.07);
}
.s-sugar-ui-input-navigation__list-item--selected {
  background-color: #2b3438 !important;
  color: white;
}
.s-sugar-ui-input-navigation__list-item-title {
  font-weight: bold;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
  margin-bottom: 5px;
}
.s-sugar-ui-input-navigation__list-item-description {
  font-size: 12px;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
}
.s-sugar-ui-input-navigation__list-item-extension {
  display: inline-block;
  height: 20px;
  line-height: 20px;
  font-size: 12px;
  font-weight: bold;
  padding: 0px 4px 4px 4px;
  position: relative;
  top: -2px;
  background-color: #f2bc2b;
  color: white;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=blade] {
  background-color: #ff2d20;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=php] {
  background-color: #8892BF;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=js] {
  background-color: #f7df1e;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=node] {
  background-color: #68A063;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=css] {
  background-color: #498FE1;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=scss] {
  background-color: #CF649A;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=sass] {
  background-color: #CF649A;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=json] {
  background-color: #000000;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=jpg] {
  background-color: #B2C0E1;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=jpeg] {
  background-color: #B2C0E1;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=pdf] {
  background-color: #E7786E;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=doc] {
  background-color: #60D7FD;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=psd] {
  background-color: #F9D659;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=mp3] {
  background-color: #E98C61;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=png] {
  background-color: #C29DFB;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=aac] {
  background-color: #B1C5C9;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=zip] {
  background-color: #9CC04E;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=dmg] {
  background-color: #E36E4B;
}
.s-sugar-ui-input-navigation__list-item-highlight {
  background-color: #f2bc2b;
  color: white;
  position: relative;
  display: inline-block;
  padding: 0 4px;
}

