}
.s-sugar-ui-input-navigation__list-item-extension[extension=png] {
  background-color: #C29DFB;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=aac] {
  background-color: #B1C5C9;
}
.s-sugar-ui-input-navigation__list-item-extension[extension=zip] {
